package com.vic.project.app_moviesimport android.app.Applicationimport android.content.Contextimport com.vic.project.app_movies.data.local.dao.MovieCacheMetaDaoimport com.vic.project.app_movies.data.local.dao.MovieDaoimport com.vic.project.app_movies.data.local.dao.MovieDetailDaoimport com.vic.project.app_movies.data.local.db.AppDatabaseimport com.vic.project.app_movies.data.local.db.DatabaseDriverFactoryimport com.vic.project.app_movies.data.local.source.LocalDataSourceimport com.vic.project.app_movies.di.initKoinimport org.koin.dsl.moduleclass MyApp : Application() {    override fun onCreate() {        super.onCreate()        initKoin(            listOf(dataModule(this))        )    }}fun dataModule(context: Context) = module {    single { DatabaseDriverFactory(context) }    single { AppDatabase(get<DatabaseDriverFactory>().create()) }    single { MovieDao(get()) }    single { MovieDetailDao(get()) }    single { MovieCacheMetaDao(get()) }    single {        LocalDataSource(            movieDao = get(),            detailDao = get(),            cacheMetaDao = get(),            logger = get()        )    }}