package com.vic.project.app_movies.screens.homeimport androidx.compose.foundation.Imageimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.Spacerimport androidx.compose.foundation.layout.aspectRatioimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.heightimport androidx.compose.material3.MaterialThemeimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.res.painterResourceimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.text.style.TextAlignimport androidx.compose.ui.unit.dpimport com.vic.project.app_movies.R@Composablefun SearchNewsNotFound(    isLoading: Boolean,    isNotFound: Boolean,) {    Column(        modifier = Modifier            .fillMaxWidth(),        horizontalAlignment = Alignment.CenterHorizontally    ) {        if (isLoading){            LoadingPage()        }        Spacer(Modifier.height(32.dp))        Image(            painter = painterResource(R.drawable.img_error_page),            contentDescription = "",            modifier = Modifier                .fillMaxWidth(0.35f)                .aspectRatio(1f)        )        Spacer(Modifier.height(16.dp))        if (isNotFound) {            Text(                text = "No movie found",                style = MaterialTheme.typography.labelLarge,                fontWeight = FontWeight.W600,                color = Color.White,                textAlign = TextAlign.Center            )            Spacer(Modifier.height(8.dp))            Text(                text = "Please search for movie with a different keyword or topic.",                style = MaterialTheme.typography.bodySmall,                fontWeight = FontWeight.W400,                color = Color.White,                textAlign = TextAlign.Center            )        } else {        Text(            text = "There is no news at the moment.",            style = MaterialTheme.typography.bodyLarge,            fontWeight = FontWeight.W400,            color = Color.White,            textAlign = TextAlign.Center,        )    }    }}