package com.vic.project.app_movies.data.mapperimport com.vic.project.app_movies.utils.StringUtils.formatLargeNumberimport com.vic.project.app_movies.utils.StringUtils.formatVoteAverageimport com.vic.project.app_movies.data.remote.dto.MovieDetailResponseimport com.vic.project.app_movies.domain.model.MovieDetailfun MovieDetailResponse.toDomainMovieDetail(): MovieDetail {    return MovieDetail(        id = id,        runtime = runtime,        title = title,        description = tagline,        overview = overview,        posterUrl = poster_path.let { "https://image.tmdb.org/t/p/w500$it" },        backdropPath = backdrop_path.let { "https://image.tmdb.org/t/p/w500$it" },        imdbId = imdb_id.let { "https://www.imdb.com/title/$it" },        genres = genres            .mapNotNull { it.name?.takeIf { name -> name.isNotBlank() } }            .joinToString(", "),        vote_average = vote_average.formatVoteAverage(),        vote_count = vote_count.formatLargeNumber(),        releaseDate = release_date    )}