package com.vic.project.app_movies.data.mapperimport com.vic.project.app_movies.utils.StringUtils.formatLargeNumberimport com.vic.project.app_movies.utils.StringUtils.formatVoteAverageimport com.vic.project.app_movies.data.remote.dto.MovieDtoimport com.vic.project.app_movies.domain.model.Moviefun MovieDto.toDomainModel(): Movie {    return Movie(        id = id,        title = title,        posterUrl = posterPath?.let { "https://image.tmdb.org/t/p/w500$it" },        rating = voteAverage.formatVoteAverage(),        vote = voteCount.formatLargeNumber(),        releaseDate = releaseDate?.split("-").orEmpty().firstOrNull()    )}