package com.vic.project.app_movies.data.remote.dtoimport kotlinx.serialization.SerialNameimport kotlinx.serialization.Serializable@Serializabledata class MovieDetailResponse(    @SerialName("backdrop_path")    val background: String?,    val genres: List<Genre>?,    val id: Int,    val imdb_id: String?,    val overview: String?,    @SerialName("poster_path")    val poster: String?,    val release_date: String?,    val runtime: Int?,    val tagline: String?,    val title: String?,    val vote_average: Double?,    val vote_count: Int?,)