package com.vic.project.app_movies.data.remote.dtoimport kotlinx.serialization.SerialNameimport kotlinx.serialization.Serializable@Serializabledata class MoviesResponse(    val page: Int,    val results: List<MovieDto>,    @SerialName("total_pages")    val totalPages: Int,    @SerialName("total_results")    val totalResults: Int,    val success: Boolean,    val status_message: String,)@Serializabledata class MovieDto(    val id: Int,    val title: String,    @SerialName("poster_path")    val posterPath: String? = null,    @SerialName("vote_average")    val voteAverage: Double,    @SerialName("vote_count")    val voteCount: Int,    @SerialName("release_date")    val releaseDate: String? = null)