package com.vic.project.app_movies.data.remote.errorimport io.ktor.client.plugins.ClientRequestExceptionimport io.ktor.client.plugins.RedirectResponseExceptionimport io.ktor.client.plugins.ServerResponseExceptionimport kotlinx.coroutines.TimeoutCancellationExceptionimport kotlinx.io.IOExceptionobject ErrorHandler {    fun handleException(throwable: Throwable): ApiException {        return when (throwable) {            is ClientRequestException -> {                ApiException.HttpError(throwable.response.status.value, null)            }            is ServerResponseException -> {                ApiException.HttpError(throwable.response.status.value, null)            }            is RedirectResponseException -> {                ApiException.HttpError(throwable.response.status.value, null)            }            is TimeoutCancellationException -> ApiException.Timeout            is IOException -> ApiException.NetworkUnavailable            else -> ApiException.Unexpected        }    }}