package com.vic.project.app_movies.data.remote.serviceimport com.vic.project.app_movies.data.remote.dto.MovieDetailResponseimport com.vic.project.app_movies.data.remote.dto.MoviesResponseimport com.vic.project.app_movies.utils.handleBodyimport io.ktor.client.HttpClientimport io.ktor.client.request.getimport io.ktor.client.request.urlimport io.ktor.http.takeFrominternal class MovieService(    private val client: HttpClient, private val baseUrl: String = "https://api.themoviedb.org/3") {    suspend fun getMovies(search: String): MoviesResponse = client.get {        url {            takeFrom("$baseUrl/search/movie")            parameters.append("query", search)            parameters.append("include_adult", "false")            parameters.append("language", "en-US")            parameters.append("page", "1")        }    }.handleBody()    suspend fun getMoviesTrending(): MoviesResponse = client.get {        url {            takeFrom("$baseUrl/trending/movie/day")            parameters.append("page", "1")            parameters.append("language", "en-US")        }    }.handleBody()    suspend fun getMovie(movieId: Int): MovieDetailResponse = client.get {        url("$baseUrl/movie/$movieId")    }.handleBody()}