package com.vic.project.app_movies.domain.usecaseimport com.vic.project.app_movies.domain.model.Movieimport com.vic.project.app_movies.domain.repository.MovieRepositoryimport com.vic.project.app_movies.utils.ResultWrapperimport kotlinx.coroutines.flow.Flowinternal class GetTrendingMoviesUseCase(    private val repository: MovieRepository) {    suspend operator fun invoke(): Flow<ResultWrapper<List<Movie>>> {        return repository.getTrendingMovies()    }}